document.querySelectorAll(".copy-btn").forEach((function(t){t.addEventListener("click",(function(){var e=t.getAttribute("data-plain"),n=t.getAttribute("data-html");"permalink"===t.getAttribute("data-copy-type")&&(e="".concat(window.location.origin,"/").concat(e));var o=new ClipboardItem({"text/plain":new Blob([e],{type:"text/plain"}),"text/html":new Blob([n||e],{type:"text/html"})});navigator.clipboard.write([o]).then((function(){var e=document.createElement("div");e.className="tooltip rounded-xl bg-gray-800 text-white text-sm px-2 py-1 absolute z-99",e.textContent="Copied!",t.appendChild(e),setTimeout((function(){e.remove()}),1500)})).catch((function(t){return console.error("Failed to copy",t)}))}))}));