document.querySelectorAll(".copy-btn").forEach((t=>{t.addEventListener("click",(()=>{let e=t.getAttribute("data-plain");const o=t.getAttribute("data-html");"permalink"===t.getAttribute("data-copy-type")&&(e=`${window.location.origin}/${e}`);const n=new ClipboardItem({"text/plain":new Blob([e],{type:"text/plain"}),"text/html":new Blob([o||e],{type:"text/html"})});navigator.clipboard.write([n]).then((()=>{const e=document.createElement("div");e.className="tooltip rounded-xl bg-gray-800 text-white text-sm px-2 py-1 absolute z-99",e.textContent="Copied!",t.appendChild(e),setTimeout((()=>{e.remove()}),1500)})).catch((t=>console.error("Failed to copy",t)))}))}));