document.querySelectorAll(".copy-plain").forEach((t=>{t.addEventListener("click",(()=>{const e=t.getAttribute("data-plain");navigator.clipboard.writeText(e).then((()=>alert("Copied plain text!"))).catch((t=>console.error("Failed to copy plain text",t)))}))})),document.querySelectorAll(".copy-rich").forEach((t=>{t.addEventListener("click",(()=>{const e=t.getAttribute("data-plain"),o=t.getAttribute("data-html"),a=new ClipboardItem({"text/plain":new Blob([e],{type:"text/plain"}),"text/html":new Blob([o],{type:"text/html"})});navigator.clipboard.write([a]).then((()=>alert("Copied as rich text!"))).catch((t=>console.error("Failed to copy rich text",t)))}))})),document.querySelectorAll(".copy-md").forEach((t=>{t.addEventListener("click",(()=>{const e=t.getAttribute("data-md");navigator.clipboard.writeText(e).then((()=>alert("Copied markdown!"))).catch((t=>console.error("Failed to copy markdown",t)))}))}));